definition.script:
pipeline {
    agent any
    stages {
        stage('Clean Workspace') {
            steps {
                sh 'rm -rf ${WORKSPACE}/* ${WORKSPACE}/.[!.]*'
            }
        }
        stage('Pull Code') {
            steps {
                git credentialsId: "MOOCODER", url: "${GIT_URL}"
            }
        }
        stage('Reset Git HEAD') {
            steps {
                sh "git reset --hard ${COMMIT_HASH}"
            }
        }
        stage('Run Build') {
            agent {
                docker {
                    image "${DOCKER_IMAGE}"
                    args '--network none --cpus 1 -m 1G -h localhost'
                    reuseNode true
                }
            }
            steps {
                echo '==CONSOLE OUTPUT BEGIN=='
                catchError {
                    timeout(1) {
                        sh "${EXECUTE_SHELL}"
                    }
                }
                echo '==CONSOLE OUTPUT END=='
            }
        }
        stage('Collect Test Report') {
            when {
                environment name: 'PROB_TARGET', value: 'TEST'
            }
            steps {
                junit allowEmptyResults: true, testResults: 'test-reports/*.xml'
            }
        }
        stage('Collect Coverage Report') {
            when {
                environment name: 'PROB_TARGET', value: 'COVERAGE'
            }
            steps {
                cobertura autoUpdateHealth: false, autoUpdateStability: false, coberturaReportFile: 'coverage-reports/*.xml', conditionalCoverageTargets: '70, 0, 0', failNoReports: false, failUnhealthy: false, failUnstable: false, lineCoverageTargets: '80, 0, 0', maxNumberOfBuilds: 0, methodCoverageTargets: '80, 0, 0', onlyStable: false, sourceEncoding: 'ASCII', zoomCoverageChart: false
            }
        }
        stage('Collect Mutation Report') {
            when {
                environment name: 'PROB_TARGET', value: 'MUTATION'
            }
            steps {
                archiveArtifacts allowEmptyArchive: true, artifacts: 'mutation-reports/mutations.xml'
            }
        }
    }
    post {
        always {
            sh 'curl -o /dev/null -s -X POST -H "X-Moocoder-Secret: ${MOOCODER_SECRET}" "${MOOCODER_HOST}/internal/notify/callback/${JOB_NAME}/${BUILD_NUMBER}" || :'
        }
    }
}
